mapper.insert:

insert into user (id, user_name, sex, 
  password, telphone, avatar, 
  type, case_example, create_time
  )
values (#{id,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{sex,jdbcType=INTEGER}, 
  #{password,jdbcType=VARCHAR}, #{telphone,jdbcType=VARCHAR}, #{avatar,jdbcType=VARCHAR}, 
  #{type,jdbcType=CHAR}, #{caseExample,jdbcType=DECIMAL}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
  	SELECT

  	FROM
  		user
  	WHERE
   		telphone = #{telphone,jdbcType=VARCHAR}

  			AND
	        user_name = #{userName,jdbcType=VARCHAR}


	    	AND
  			sex = #{sex,jdbcType=INTEGER}


   	 		AND
        	password = #{password,jdbcType=VARCHAR}


      		AND
	        avatar = #{avatar,jdbcType=VARCHAR}


     		AND
	        type = #{type,jdbcType=CHAR}


      		AND
	        case_example = #{caseExample,jdbcType=DECIMAL}


      		AND
	        create_time = #{createTime,jdbcType=TIMESTAMP}
