mapper.select:
	SELECT
		u.id, u.user_name userName, u.sex, u.password, 
		u.telphone, u.avatar, u.type, u.positional_titles positionalTitles, 
		u.case_example caseExample, u.status, u.create_time createTime,
		`is`.path avatarPath
  FROM 
  	user u
  	LEFT JOIN
  		img_source `is`
  		ON
  			u.avatar = `is`.id
  WHERE
  	1=1
 	
 		AND
      u.id = #{id}
  
  
  	AND
      u.user_name = #{userName}
  
  
  	AND
      u.sex = #{sex}
  
  
  	AND
      u.password = #{password}
  
  
    	AND
      u.telphone = #{telphone}
  
  
    	AND
    	u.avatar ={avatar}
  
  
  	AND
    	u.type = #{type}
  
  
  	AND
    	u.positional_titles = #{positionalTitles}
  
  
      AND
      u.case_example = #{caseExample}
  
  
  	AND
    	u.status = #{status}
  
  
  	AND
    	u.create_time = #{createTime}
